const api = require('../lib/api')

const Index = () => {

  return <span>Index</span>
}

Index.getInitialProps = async ({ ctx }) => {
  const result = await api.request(
    {
      url: '/search/repositories?q=react'
    }, 
    ctx.req, 
    ctx.res
  )

  return {
    data: result.data
  }
}

export default Index
